        //   // //  <View>
        //   // //   <View style={styles.containerBlockBackground}>
        //   // //     <SvgCategoryBackground height={bgWidth - 2} width={bgWidth} />
        //   // //   </View>
        //   // //   <Image source={images[item.image]} style={styles.image} />
        //   // //   </View>
        //   //   // console.log("image",image,"hello",images)
        //   // )}
        //   renderItem={({ item: { image } }) => (
        //     // console.log("image",image,"hello",images)
        //     <Image source={images[image]} width={itemWidth} />
        //   )}
        //   sliderWidth={device.width}
        //   itemWidth={bgWidth}
        // />
        // );
  //     })}
  //   </View>
  // );
  return (
    // <View>
    //   <Carousel
    //      autoplay
    //      autoplayInterval={1000}
    //     ref={isCarousel}
    //     data={categoriesData}
    //     renderItem={CarouselCardItem}
    //     itemWidth={ITEM_WIDTH}
    //     inactiveSlideShift={0}
    //     style={{backgroundSize: 'auto',}}
    //     useScrollView={true}
    //     onSnapToItem = { index => this.setState({activeIndex:index}) }
    //   />
    // </View>
    <Carousel {...settings}>
    {/* update the settings by passing the setting object as props */}
    {
      dataset1.map((data,index)=>(
       <Wrap key={index}>
         
        <a alt="image">
        <View style={styles.bannertext}>
          <p>{myIcon}{data.rating}/10</p>
          <h1>{data.name}</h1>
          <p>{data.description}</p>
          <Button style={styles.watchnow}>Watch Now</Button>
          </View>
          <img src={data.backdrop} alt="" />
        </a>
      </Wrap>
      )
 
      )
    }
   
    {/* <Wrap>
      <a alt="image">
        <img src={example2} alt="" />
      </a>
    </Wrap>
    <Wrap>
      <a>
        <img src={example3} alt="" />
      </a>
    </Wrap>
    <Wrap>
      <a alt="image">
        <img src={example4} alt="" />
      </a>
    </Wrap> */}
  </Carousel>
  )
};
const Carousel = styled(Slider)`
  margin-top: 20px;
  ${"" /* Overwriting the styles */}

  & > button {
    opacity: 0;

    height: 100%;
    width: 5vw;
    z-index: 1;
  }

  &:hover {
    & > button {
      opacity: 1;
      ${
        "" /* 0.4s represents time taken for transition and 0s represents after how much time transition will start or take effect */
      }
      transition: opacity 0.4s ease 0s;
    }
  }

  ul li button {
    &:before {
      font-size: 10px;
      color: rgb(150, 158, 171);
    }
  }

  li.slick-active button:before {
    color: white;
  }

  .slick-list {
    overflow: initial;
  }
  .slick-prev {
    left: -60px;
  }
  .slick-next {
    right: -60px;
  }
`;

const Wrap = styled.div`
  border-radius: 4px;
  cursor: pointer;
  position: relative;

  a {
    border-radius: 4px;
    ${"" /* This shadow code can created using Online calculators*/}
    box-shadow: rgb(0 0 0 / 69%) 0px 26px 30px -10px,
      rgb(0 0 0 / 73%) 0px 16px 10px -10px;
    cursor: pointer;
    display: block;
    position: relative;
    padding: 4px;
    img {
      width: 100%;
      height: 500px;
    }
    &:hover {
      padding: 0;
      border: 4px solid rgba(249, 249, 249, 0.8);
      transition-duration: 300ms;
    }
  },
  
`;
const styles = {
  bannertext: {
    position: 'absolute',
    top: '20%',
    padding: 20,
    color: 'white' 
    },
    watchnow: {
      textAlign:'left',
    }
  }
// const styles = StyleSheet.create({
