import React from 'react'
import { Text, View } from 'react-native-web'
import { TouchableOpacity, StyleSheet, FlatList ,ImageBackground} from 'react-native'
import PropTypes from 'prop-types';
import SegmentedControlTab from 'react-native-segmented-control-tab';
const image = { uri: "https://reactjs.org/logo-og.png" };
import { useState } from 'react';


const MovieDetail1 = ({ navigation }) => {
    const [segmentIndex,setSegmentIndex]=useState(0)
   const handleIndexChange = index => {
    setSegmentIndex(index)
          };

    return(
        <View style={styles.container}>
             <ImageBackground source={image} resizeMode="cover" style={styles.image}>
            <View style={styles.detail}>
                <h1>Parampara</h1>
                <View>
                    <p>1 Season </p>
                    <p>7 Episodes </p>
                    <p>Drama </p>
                    <p>A </p>
                    <p> Hotstar Specials </p>
                </View>
                <p>For years, Naidu has kept everyone under his ruthless rule, until his nephew, Gopi, decides to challenge him and bring down his empire of injustice.</p>
            </View>
            <View>
                <TouchableOpacity>
                    <Text>
                        Play
                    </Text>
                </TouchableOpacity>
                <View>
                    +
                    <Text>
                        Wishlist
                    </Text>
                </View>
                <View>

                    <Text>
                        Share
                    </Text>
                </View>
            </View>
            <View>
            <FlatList
                data={[
                {key: 'తెలుగు'},
                {key: 'मराठी'},
                {key: 'हिंदी'},
                {key: 'தமிழ்'},
                {key: 'ಕನ್ನಡ'},
                {key: 'বাংলা'},
                {key: 'മലയാളം'},
                ]}
                renderItem={({item}) => <Text style={styles.item}>{item.key}</Text>}
            />
            </View>

            <SegmentedControlTab
          values={["First", "Second", "Third"]}
          selectedIndex={segmentIndex}
          onTabPress={handleIndexChange}
        />
            </ImageBackground>
        </View>
    )
}
    // const[selectedIndex,setSelectedIndex]=useState(0)

;

MovieDetail1.propTypes = {
  // required
  navigation: PropTypes.object.isRequired
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
      },
    detail: {
      width: '100%',
      alignItems: 'flex-end',
      marginBottom: 24,
    },
    banner: {

    }
  })

export default MovieDetail1;
