import React, { useState, useEffect } from 'react';
import { StyleSheet, Text,FlatList,  View,TouchableOpacity, Image } from 'react-native';
import { ScrollView } from 'react-navigation';
import { device, gStyle } from '../constants';
import BackButton from '../components/BackButton';
import Icon from 'react-native-vector-icons/Ionicons';

// components
import Categories from '../components/Categories';
import MediaItemScroller from '../components/MediaItemScroller';
import SlideShow from '../components/SlideShow';
import Icon1 from 'react-native-vector-icons/FontAwesome';
// icons
import SvgBackground from '../components/icons/Svg.Background';
// import SvgDisneyPlusLogo from '../components/icons/Svg.DisneyPlusLogo';
import SvgDisneyPlusLogo from '../assets/flicknplay.png';
const myIcon = <Icon name="home" size={30} color="#900" />;
const myIcon1 = <Icon name="download" size={30} color="#900" />;
const search = <Icon name="search-circle" size={30} color="#900" />;
const profile = <Icon1 name="user-circle" size={30} color="#900" />;

const axios = require('axios');
const Home = ({navigation}) => {
  // console.log( navigation.openDrawer())
  const [data,ListData]=useState([])
  useEffect(()=>{
    axios.get(`https://www.flicknplay.com/secure/homepage/lists`).then((response)=>{
     
         console.log(response.data.lists)
         ListData(response.data.lists)
       })
       
  },[])
  const test=()=>{
    console.log('test')
  }
  return (
  <View style={gStyle.container}>
    {/* <View style={styles.list}>
      <FlatList
        data={[
          {key: 'Home' },
          {key: 'Search'},
          {key: 'Download'},
          {key: 'Profile'},
        ]}
        renderItem={({item}) => <Text style={styles.item}>{myIcon} {item.key} </Text>}
      />
      </View> */}
  {/* <View style={gStyle.posAbsolute}>
    <SvgBackground />
  </View> */}
  
  <ScrollView scrollEventThrottle={16} showsVerticalScrollIndicator={false}>
  
    <View style={styles.containerHeader} >
     
   {/* <SvgDisneyPlusLogo/> */}
   <TouchableOpacity  onPress={() => navigation.openDrawer()}>
   <div style={styles.buttons} >
                              <Image
        style={styles.image}
        source={require('../assets/menu.png')}
      />
                            </div>
  
                            </TouchableOpacity> 
    <img  style={{margin: '30px 64px'}}src={SvgDisneyPlusLogo}  className='img-fluid' width={200}/> 
 
    </View>

    <SlideShow />

    <Categories />

    {/* <Text style={gStyle.heading}>Originals</Text>
    <MediaItemScroller dataset="originals" />

    <Text style={gStyle.heading}>Recommended For You</Text>
    <MediaItemScroller dataset="recommended" />

    <Text style={gStyle.heading}>Hit Movies</Text>
    <MediaItemScroller dataset="hits" />

    <Text style={gStyle.heading}>Trending</Text>
    <MediaItemScroller dataset="trending" />

    <Text style={gStyle.heading}>Out of the Vault</Text>
    <MediaItemScroller dataset="vault" />

    <Text style={gStyle.heading}>Ultra HD and HDR</Text>
    <MediaItemScroller dataset="hdr" /> */}
   {data.map((value, index) => {
     { console.log(value) }
     return <View key={value.id}>
         <Text style={gStyle.heading}>{ value.name } </Text>
         <MediaItemScroller navigation={navigation} dataset={value?.items} />
      </View>
      })}
 
    <View style={gStyle.spacer24} />
  </ScrollView>
</View>
);
}
 

const styles = {
  containerHeader: {
    alignItems: 'left',
    marginBottom: 8,
    marginleft:'20px',
    paddingTop: device.iPhoneNotch ? 36 : 6
  },
  bar: {
    fontSize: 20 ,
    zIndex: '9',
    color: 'white'
  },
  image: {
    zIndex: 9,
    position: 'relative'
  },
  buttons: {
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 12,
    paddingHorizontal: 12,
    borderRadius: 4,
    marginRight: 10,
    width: 53,
    height: 45,
    elevation: 3,
    marginTop: 28,
    position:'absolute',
    top: '2%',
    zIndex:'6',
    backgroundColor: '#202024',
  },
  item:{
    color: 'white',
    fontSize: 20,
    paddingHorizontal: 30,
    paddingVertical: 15
  },
  list: {
    backgroundColor: '#202024'
  }
};

export default Home;
