import styled from "styled-components";
// import aa from '../assets/'
import disneyImage from "../assets/viewers-disney.png"
import disneyVideo from "../assets/videos/1564674844-disney.mp4"
const Viewers = () => {
  return (
    <Container>
      <Wrap>
        <img src={disneyImage} alt="" />
        <video autoPlay={true} muted={true} loop={true} playsInline={true}>
          <source src="../assets/videos/1564674844-disney.mp4" type="video/mp4" />
        </video>
      </Wrap>
      <Wrap>
        {/* Chrome does not allow any video to be autoplayed if it's not 'muted' */}
        <img src="../assets/viewers-pixar.png" alt="" />
        <video autoPlay={true} muted={true} loop={true} playsInline={true}>
          <source src="../assets/videos/1564676714-pixar.mp4" type="video/mp4" />
        </video>
      </Wrap>
      <Wrap>
        <img src="../assets/viewers-marvel.png" alt="" />
        <video autoPlay={true} muted={true} loop={true} playsInline={true}>
          <source src="../assets/videos/1564676115-marvel.mp4" type="video/mp4" />
        </video>
      </Wrap>
      <Wrap>
        <img src="../assets/viewers-starwars.png" alt="" />
        <video autoPlay={true} muted={true} loop={true} playsInline={true}>
          <source src="../assets/videos/1608229455-star-wars.mp4" type="video/mp4" />
        </video>
      </Wrap>
      <Wrap>
        <img src="../assets/viewers-national.png" alt="" />
        <video autoPlay={true} muted={true} loop={true} playsInline={true}>
          <source
            src="../assets/videos/1564676296-national-geographic.mp4"
            type="video/mp4"
          />
        </video>
      </Wrap>
    </Container>
  );
};

const Container = styled.div`
  margin-top: 4px;
  padding: 30px 0px 26px;
  display: grid;
  grid-gap: 25px;
  grid-template-columns: repeat(5, minmax(0, 1fr));
  @media (max-width: 768px) {
    grid-template-columns: repeat(1, minmax(0, 1fr));
  }
`;

const Wrap = styled.div`
  padding-top: 56.25%;
  border-radius: 10px;
  box-shadow: rgb(0 0 0 / 69%) 0px 26px 30px -10px,
    rgb(0 0 0 / 73%) 0px 16px 10px -10px;
  cursor: pointer;
  overflow: hidden;
  position: relative;
  transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
  border: 3px solid rgba(249, 249, 249, 0.1);

  img {
    inset: 0px;
    display: block;
    height: 100%;
    ${
      "" /* the image keeps its aspect ratio and fills the given dimension. The image will be clipped to fit: */
    }
    object-fit: cover;
    opacity: 1;
    position: absolute;
    transition: opacity 500ms ease-in-out 0s;
    width: 100%;
    z-index: 1;
    top: 0;
  }
  video {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0px;
    opacity: 0;
    z-index: 0;
  }
  &:hover {
    box-shadow: rgb(0 0 0 / 80%) 0px 40px 58px -16px,
      rgb(0 0 0 / 72%) 0px 30px 22px -10px;
    transform: scale(1.05);
    border-color: rgba(249, 249, 249, 0.8);
    video {
      opacity: 1;
    }
  }
`;

export default Viewers;
