                })
                response.data.title.seasons.map((data, index) => {
                    seasonsList.push(data.model_type + ' ' + data.number)
                })

            }
            if (response.data.title.videos.length != 0) {
                response.data.title.videos.map((data, item) => {

                    if (data.type === "video") {
                        setVideoUrl(data)
                    }
                })
            }
            let directors = []
            let casts = []
            let writers = []
            if (response.data.title.credits.length != 0) {
                response.data.title.credits.map((data, item) => {

