  }

};

const styles = StyleSheet.create({
  container: {
    alignItems: 'flex-start',

    justifyContent: 'space-between',
    paddingBottom: 8,
    paddingLeft: 16,
    paddingTop: 24
  },
  containerBlock: {
    alignItems: 'center',
    borderColor: colors.categoryBorder,
    borderRadius: 4,
    borderWidth: 1,
    flex: 1,
    justifyContent: 'center',
    marginRight: 16
  },
  containerBlockBackground: {
    borderRadius: 2,
    overflow: 'hidden',
    position: 'absolute'
  },
  image: {
    height: 100,
    width: 200
  }
});

export default Categories;
