import React, { useState, useEffect } from 'react';
import { Text, View } from 'react-native-web'
import { TouchableOpacity, Image, Pressable, ImageBackground, FlatList,ScrollView, SafeAreaView, StatusBar  } from 'react-native'
import PropTypes from 'prop-types';
import { colors, images } from '../constants';
import SegmentedControlTab from 'react-native-segmented-control-tab';
const axios = require('axios');
import BackButton from '../components/BackButton';
// import Modal from "react-native-modal";
import AsyncStorage from '@react-native-community/async-storage';
import Icon from 'react-native-vector-icons/FontAwesome';
// import VideoPlayer from 'expo-video-player'
import { VirtualizedListCellContextProvider } from 'react-native/Libraries/Lists/VirtualizedListContext';
const myIcon = <Icon name="play" size={20} color="#333" />;
const image = { uri: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ9VZB5hU9xt5HU8MQjHIzLVHU8RDRhu1yVHg&usqp=CAU" };
const MovieDetail1 = ({ navigation }) => {
    const movieId=navigation.state.params.movieId
    // console.log(movieId)
    const [segmentIndex, setSegmentIndex] = useState(0)
    const [id, setId] = useState(0)
    const imageList = [
        {
            "imageName": "https://source.unsplash.com/1024x768/?nature"
        },
        {
            "imageName": "https://source.unsplash.com/1024x768/?water"
        },
        {
            "imageName": "https://source.unsplash.com/1024x768/?girl"
        },
        {
            "imageName": "https://source.unsplash.com/1024x768/?tree"
        }

    ]
    const [data, setData] = useState()

    const [isModalVisible, setModalVisible] = useState(false);

    const toggleModal = () => {
        setModalVisible(!isModalVisible);
    };
    const handleIndexChange = index => {
        console.log(index)
        setSegmentIndex(index)
    };
    useEffect(async () => {
        // setId(navigation.state.params.id)
        // console.log(navigation.state.params.id)
        // const token = await AsyncStorage.getItem('user_token')
        const token = '190|oSwWLmwKojhwkjKhmQF1Po8BHJhhckHHhsoVlo1i'
        // console.log(token)
        axios.get(`https://www.flicknplay.com/secure/titles/+${movieId}`).then((response) => {

            console.log(response)
            setData(response.data)
        })
    }, [])
    const renderItem = ({ item }) => {
        return (
            <View style={styles.item}>
                <Image source={item.imageName} style={styles.images}></Image>
            </View>
            //   <TouchableOpacity  onPress={()=>{
            //     navigation.navigate('MovieDetail1')
            //   console.log(item.id)
            //   }}>
            // <View style={styles.item} >
            //     <Image  source={item?.poster} style={styles.image} />
            //      </View>
            //      </TouchableOpacity
        )
    }
    return (
        <ScrollView style={styles.scrollView}>
            <ImageBackground source={{ uri: data.title.backdrop }} resizeMode="cover" style={styles.images}>
            <BackButton style={{zIndex:1}}  goBack={() => navigation.replace('Home')} />
                <View style={[ styles.overlay]}>

                    <View style={styles.container}>
                           <h1 style={{ paddingTop: '10%',}}>The Suicide Squad</h1>
                        <View style={{ display: 'block' }}>
                            <Text style={styles.age}>12</Text>
                            <Text style={styles.age}>AD)))</Text>
                            <Text style={styles.age}>CC</Text>
                            <Text style={styles.age1}>{data.title.year}</Text>
                            <Text style={styles.age1}>{data.title.runtime}</Text>

                        </View>
                        <p >{data.title.genres.map(genre => {
                            return <Text key={genre.id} style={{ color: 'white' }}>{genre.display_name} </Text>
                        })}</p>
                        <View style={styles.play}>
                            <Pressable style={styles.button} >
                                <Text style={styles.text} onPress={()=>navigation.navigate('ModalVideo')}>{myIcon}Play</Text>
                            </Pressable>

                            <Pressable style={styles.buttons} >
                                <Text style={styles.texts} onPress={() => { navigation.navigate('ModalAddProfile') }}> TRAILER</Text>
                            </Pressable>
                            <Icon name="plus" backgroundColor="#333" style={styles.wishlist}>
                            </Icon>
                            <Icon name="users" backgroundColor="#333" style={styles.wishlists}>
                            </Icon>

                        </View>
                        <p style={styles.description}>{data.title.description}.</p>
                        <SegmentedControlTab
                            tabsContainerStyle={styles.tabsContainerStyle}
                            tabStyle={styles.tab}
                            firstTabStyle={styles.firstTabStyle}
                            lastTabStyle={styles.lastTabStyle}
                            tabTextStyle={styles.tabTextStyle}
                            activeTabStyle={styles.activeTabStyle}
                            activeTabTextStyle={styles.activeTabTextStyle}
                            selectedIndex={1}
                            allowFontScaling={false}
                            values={["SUGGESTED", "EXTRAS", "DETAILS"]}
                            selectedIndex={segmentIndex}
                            onTabPress={handleIndexChange}
                        />
                        <hr style={styles.hrr1}/>
                        <View style={styles.tabbody}>
                            {segmentIndex == 0 &&
                                <FlatList
                                    contentContainerStyle={styles.containerFlatList}
                                    data={imageList}
                                    horizontal
                                    keyExtractor={item => item.id}
                                    renderItem={renderItem}

                                    showsHorizontalScrollIndicator={false}
                                />

                            }
                            {segmentIndex == 1 &&
                                <View style={styles.tabbody}>
                                     <h1>hello1</h1>
                                     </View>

                            }
                            {segmentIndex == 2 &&
                             <View style={styles.tabbody}>
                               <View>
                                   <h3 style={styles.marg}>Director</h3>
                                   <p>James Gunn </p>
                                   <hr style={styles.hrr}/>
                                   <h3 style={styles.marg}>Writers</h3>
                                   <p>John Ostrander,Kim Yale,James Gunn</p>
                                   <hr style={styles.hrr}/>
                                   <h3 style={styles.marg}>Cast</h3>
                                   <p>Margot Robbie,Idris Elba,John Cena</p>
                                   <hr style={styles.hrr}/>
                                   <h3 style={styles.marg}>Plot</h3>
                                   <p>
                                        Supervillains Harley Quinn, Bloodsport, Peacemaker and a collection of nutty cons
                                        at Belle Reve prison join the super-secret, super-shady
                                        Task Force X as they are dropped off at the remote, enemy-infused island of Corto Maltese.
                                   </p>
                                   </View>
                                </View>

                            }
                        </View>
                    </View>

                </View>
            </ImageBackground>
            </ScrollView>
    )
}
    // const[selectedIndex,setSelectedIndex]=useState(0)

    ;

MovieDetail1.propTypes = {
    // required
    navigation: PropTypes.object.isRequired
};

const styles = {
    container: {
        flex: 1,
        paddingHorizontal: '5%',
        paddingTop: StatusBar.currentHeight,
        color: 'white',
    },
    age: {
        background: 'black',
        padding: 4,
        margin: 5,
        color: '#fff'
    },
    description: {
       width:'60%',
    },
    age1: {
        padding: 4,
        margin: 5,
        color: '#fff'
    },
    images: {
        flex: 1,
        justifyContent: "center",
        width: '100%',
        backgroundsize: 'cover',
        backgroundPosition: 'top',
    },
    button: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 12,
        borderRadius: 4,
        marginRight: 10,
        width: 110,
        height: 45,
        elevation: 3,
        backgroundColor: 'white',
    },
    text: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: 'black',
    },
    buttons: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 12,
        borderRadius: 4,
        marginRight: 10,
        width: 110,
        height: 45,
        elevation: 3,
        backgroundColor: 'black',
    },
    texts: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: 'white',
    },
    play: {
        flex: 1,
        display: 'flex',
        flexDirection: 'row',
        maxHeight: 44,
    },
    wishlist: {
        borderRadius: 26,
        padding: 10,
        border: '2px solid white',
        height: 46,
        width: 46,
        background: 'black',
        color: 'white',
        cursor: 'pointer',
        paddingVertical: 10,
        paddingHorizontal: 12,
        marginRight: 10,
        fontSize: 22,
    },
    wishlists: {
        borderRadius: 26,
        padding: 10,
        border: '2px solid white',
        height: 46,
        width: 46,
        cursor: 'pointer',
        background: 'black',
        color: 'white',
        paddingVertical: 10,
        paddingHorizontal: 10,
        marginRight: 10,
        fontSize: 22,
    },
    tab: {
        background: 'transparent',
        border: 'none',
        width: 'auto',
        display: 'contents',
    },
    tabTextStyle: {
        fontSize: 20,
        fontWeight: 700,
        paddingRight: 20,
    },
    overlay: {
        backgroundColor: '#0000004d',
    }
    ,
    containerFlatList: {
        paddingLeft: 16,
        paddingRight: 8
    },
    item: {
        borderRadius: 4,
        height: 130,
        marginRight: 8,
        overflow: 'hidden',
        width: 93,
        marginTop:20,
    },
    placeholder: {
        backgroundColor: colors.infoGrey,
        height: '100%',
        width: '100%'
    },
    image: {
        height: '100%',
        resizeMode: 'contain',
        width: '100%'
    },
    tabbody: {
        marginTop: 10,
        marginBottom: 30,
    },
    hrr: {
        width:'100%',
        opacity:'0.4',
    },
    hrr1: {
        width:'100%',
    },
    marg: {
        margin: 0,
    }

}
export default MovieDetail1;
