import React, { Component } from 'react'
import { View, Text, StyleSheet, Dimensions } from 'react-native'
import Carousel, { Pagination } from 'react-native-snap-carousel' // 3.6.0

const SCREEN_WIDTH = Dimensions.get('window').width


const Screen = (props) => (
  <View style={{ flex : 1, backgroundColor:"#369" }}>
    <Text>{ props.text }</Text>
  </View>
);

export default class Categories extends Component {
   categoriesData = [
    { id: 1, image: 'logoDisney' },
    { id: 2, image: 'logoPixar' },
    { id: 3, image: 'logoMarvel' },
    { id: 4, image: 'logoStarWars' },
    { id: 5, image: 'logoNatGeo' }
  ];

  constructor(props) {
    super(props)
    this.state = {
      activeTab : 0
    }
  } 

  render() {
    return (
      <View style={styles.container}>


        <Carousel

          ref={ ref => this.carouselRef = ref }
          data={ this.categoriesData }
          renderItem={ ({ item }) => item }

          onSnapToItem={ i => this.setState({ activeTab : i }) }
          sliderWidth={ SCREEN_WIDTH }
          itemWidth={ SCREEN_WIDTH }
          slideStyle={{ width : SCREEN_WIDTH }}
          inactiveSlideOpacity={ 1 }
          inactiveSlideScale={ 1 }

        />

        <View
          style={ styles.tabBar }
        >
          <Pagination
            containerStyle={{ backgroundColor: 'rgba(0, 0, 0, 0.75)' }}
              dotStyle={styles.ww}
       inactiveDotOpacity={0.4}
              inactiveDotScale={0.6}
            activeDotIndex={ this.state.activeTab }


          />
        </View>


      </View>
    )
  }
}


const styles = StyleSheet.create({
  container: {
    alignItems: 'flex-start',

    justifyContent: 'space-between',
    paddingBottom: 8,
    paddingLeft: 16,
    paddingTop: 24
  },
  containerBlock: {
    alignItems: 'center',
    borderColor: colors.categoryBorder,
    borderRadius: 4,
    borderWidth: 1,
    flex: 1,
    justifyContent: 'center',
    marginRight: 16
  },
  containerBlockBackground: {
    borderRadius: 2,
    overflow: 'hidden',
    position: 'absolute'
  },
  image: {
    height: 100,
    width: 200
  }
});
