import React from 'react'
import { Text, View } from 'react-native-web'
import { TouchableOpacity, Button,Pressable ,ImageBackground} from 'react-native'
import PropTypes from 'prop-types';
import SegmentedControlTab from 'react-native-segmented-control-tab';
const image = { uri: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ9VZB5hU9xt5HU8MQjHIzLVHU8RDRhu1yVHg&usqp=CAU" };
import { useState } from 'react';
import Icon from 'react-native-vector-icons/FontAwesome';
const myIcon = <Icon name="play" size={20} color="#333" />;

const MovieDetail1 = ({ navigation }) => {
    const [segmentIndex,setSegmentIndex]=useState(0)
   const handleIndexChange = index => {
    setSegmentIndex(index)
          };
        
    return(
        <View >
             <ImageBackground source={image} resizeMode="cover" style={styles.image}>
            <View style={styles.container}>
                <View style={{display: 'block'}}>
                    <Text style={styles.age}>12</Text>
                    <Text style={styles.age}>AD)))</Text>
                    <text style={styles.age}>CC</text>
                    <Text style={styles.age1}>2013</Text>
                    <Text style={styles.age1}>2h 12m</Text>
                   
                </View>
                <p>Science Fiction,Super Hero, Action-Adventure</p>
                <View style={styles.play}>
                <Pressable style={styles.button} >
      <Text style={styles.text}>{myIcon}Play</Text>
    </Pressable>
                    
    <Pressable style={styles.buttons} >
      <Text style={styles.texts}> TRAILER</Text>
    </Pressable>
    <Icon name="plus" backgroundColor="#333" style={styles.wishlist}>
  </Icon>
  <Icon name="users" backgroundColor="#333" style={styles.wishlist}>
  </Icon>
                </View>
                <p>Tony Stark encounters a formidable foe called the Mandarin, and after failing to him, he embarks on a journey with relentless consequences.</p>
                <SegmentedControlTab
                 tabsContainerStyle={styles.tabsContainerStyle}
                 tabStyle={styles.tab}
                 firstTabStyle={styles.firstTabStyle}
                 lastTabStyle={styles.lastTabStyle}
                 tabTextStyle={styles.tabTextStyle}
                 activeTabStyle={styles.activeTabStyle}
                 activeTabTextStyle={styles.activeTabTextStyle}
                 selectedIndex={1}
                 allowFontScaling={false}
          values={["SUGGESTED", "EXTRAS", "DETAILS"]}
          selectedIndex={segmentIndex}
          onTabPress={handleIndexChange}
        />
            </View>

           
            </ImageBackground>
        </View>
    )
}
    // const[selectedIndex,setSelectedIndex]=useState(0)

;

MovieDetail1.propTypes = {
  // required
  navigation: PropTypes.object.isRequired
};

const styles = {
    container: {
        flex: 1,
        paddingTop: '16%',
        paddingLeft:60,
    color: 'white',
      },
    age: {
        background: 'black',
        padding: 4,
        margin: 5,
    },
    age1: {
        padding: 4,
        margin: 5,
    },
    image: {
        flex: 1,
        justifyContent: "center",
        height:'100vh',
        width:'100%',
        backgroundsize:'cover',
        backgroundPosition:'top',
      },
      button: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 12,
        borderRadius: 4,
        marginRight:10,
        width:110,
        elevation: 3,
        backgroundColor: 'white',
      },
      text: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: 'black',
      },
      buttons: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 12,
        borderRadius: 4,
        marginRight:10,
        width:110,
        elevation: 3,
        backgroundColor: 'black',
      },
      texts: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: 'bold',
        letterSpacing: 0.25,
        color: 'white',
      },
      play:{
        flex: 1,
        display: 'flex',
        flexDirection: 'row',
      },
      wishlist: {
        borderRadius: 26,
        padding: 10,
        border: '2px solid white',
        height:46,
        width: 46,
        background: 'black',
        color: 'white',
        marginRight: 10,
        fontSize: 22,
      },
      tab: {
        background: 'transparent',
        border: 'none',
        width: 'auto',
        display: 'contents',
      },
      tabTextStyle: {
          fontSize: 20,
          fontWeight:700,
          paddingRight:20,
      }
    
  
}
export default MovieDetail1;
