import React from 'react'
import { Text, View } from 'react-native-web'
import { TouchableOpacity, StyleSheet, FlatList } from 'react-native'
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
const image = { uri: "https://reactjs.org/logo-og.png" };
const movieDetail = ({ navigation }) =>  (
 

  
        <View style={styles.container}>
             <ImageBackground source={image} resizeMode="cover" style={styles.image}>
            <View style={styles.detail}>
                <h1>Parampara</h1>
                <View>
                    <p>1 Season </p>
                    <p>7 Episodes </p>
                    <p>Drama </p>
                    <p>A </p>
                    <p> Hotstar Specials </p>
                </View>
                <p>For years, Naidu has kept everyone under his ruthless rule, until his nephew, Gopi, decides to challenge him and bring down his empire of injustice.</p>
            </View>
            <View>
                <TouchableOpacity>
                    <Text>
                        Play
                    </Text>
                </TouchableOpacity>
                <View>
                    +
                    <Text>
                        Wishlist
                    </Text>
                </View>
                <View>
                    
                    <Text>
                        Share
                    </Text>
                </View>
            </View>
            <View>
            <FlatList
                data={[
                {key: 'తెలుగు'},
                {key: 'मराठी'},
                {key: 'हिंदी'},
                {key: 'தமிழ்'},
                {key: 'ಕನ್ನಡ'},
                {key: 'বাংলা'},
                {key: 'മലയാളം'},
                ]}
                renderItem={({item}) => <Text style={styles.item}>{item.key}</Text>}
            />
            </View>
            
            </ImageBackground>
        </View>
    
)

ModalManageProfiles.propTypes = {
  // required
  navigation: PropTypes.object.isRequired
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
      },
    detail: {
      width: '100%',
      alignItems: 'flex-end',
      marginBottom: 24,
    },
    banner: {

    }
  })
export default movieDetail
