import * as React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { ScrollView } from 'react-navigation';
import { device, gStyle } from '../constants';

// components
import Categories from '../components/Categories';
import MediaItemScroller from '../components/MediaItemScroller';
import SlideShow from '../components/SlideShow';

// icons
import SvgBackground from '../components/icons/Svg.Background';
// import SvgDisneyPlusLogo from '../components/icons/Svg.DisneyPlusLogo';
import SvgDisneyPlusLogo from '../assets/flicknplay.png';

const Home = () => (
  <View style={gStyle.container}>
    <View style={gStyle.posAbsolute}>
      <SvgBackground />
    </View>

    <ScrollView scrollEventThrottle={16} showsVerticalScrollIndicator={false}>
      <View style={styles.containerHeader}>
     {/* <SvgDisneyPlusLogo/> */}
      <img  style={{margin: '30px 0px'}}src={SvgDisneyPlusLogo} className='img-fluid' width={200}/> 
      </View>

      <SlideShow />

      <Categories />

      <Text style={gStyle.heading}>New</Text>
      <MediaItemScroller dataset="3" />

      <Text style={gStyle.heading}>Marvel Cinematic Universe</Text>
      <MediaItemScroller dataset="4" />

      <Text style={gStyle.heading}>Coming Soon</Text>
      <MediaItemScroller dataset="5" />

      <Text style={gStyle.heading}>Dc Universe</Text>
      <MediaItemScroller dataset="12" />

      <Text style={gStyle.heading}>Horror</Text>
      <MediaItemScroller dataset="102" />

      <Text style={gStyle.heading}>Quentin Tarantino Movies </Text>
      <MediaItemScroller dataset="103" />


      <Text style={gStyle.heading}>Popular </Text>
      <MediaItemScroller dataset="114" />


      <Text style={gStyle.heading}>Series </Text>
      <MediaItemScroller dataset="118" />


      <Text style={gStyle.heading}>Mind-Bending Films </Text>
      <MediaItemScroller dataset="128" />


      <Text style={gStyle.heading}>Tim Burton Films </Text>
      <MediaItemScroller dataset="131" />


      <Text style={gStyle.heading}>Horror </Text>
      <MediaItemScroller dataset="132" />


      <Text style={gStyle.heading}>Star Wars </Text>
      <MediaItemScroller dataset="133" />

      <Text style={gStyle.heading}>Disney Films </Text>
      <MediaItemScroller dataset="210" />

      <Text style={gStyle.heading}>Christmas Films </Text>
      <MediaItemScroller dataset="215" />

      <Text style={gStyle.heading}>Just Release </Text>
      <MediaItemScroller dataset="hdr" />

      <Text style={gStyle.heading}>Marvel </Text>
      <MediaItemScroller dataset="hdr" />

      <Text style={gStyle.heading}>Banner Popular Films </Text>
      <MediaItemScroller dataset="hdr" />

      <Text style={gStyle.heading}>Top Rated </Text>
      <MediaItemScroller dataset="hdr" />

      <View style={gStyle.spacer24} />
    </ScrollView>
  </View>
);

const styles = StyleSheet.create({
  containerHeader: {
    alignItems: 'center',
    marginBottom: 8,
    paddingTop: device.iPhoneNotch ? 36 : 6
  }
});

export default Home;
